import javax.swing.*;import java.awt.*;import java.awt.event.*;class DeleteFrame extends JFrame{Container c;JLabel lblrno;JTextField txtrno;JButton btnSave, btnBack;DeleteFrame(){c = getContentPane();c.setLayout(null);lblrno = new JLabel("Enter rno:");txtrno = new JTextField(35);btnSave = new JButton("Save");btnBack = new JButton("Back");lblrno.setBounds(20,10,350,30);txtrno.setBounds(20,40,350,30);btnSave.setBounds(120,150,150,50);btnBack.setBounds(120,235,150,50);c.add(lblrno);c.add(txtrno);c.add(btnSave);c.add(btnBack);ActionListener a1 = (ae) -> { MainFrame a = new MainFrame(); dispose(); };btnBack.addActionListener(a1);ActionListener a2 = (ae) -> {	try	{	int rno = Integer.parseInt(txtrno.getText());	DbHandler db = new DbHandler();	db.deleteStudent(rno);	txtrno.setText("");	}	catch(NumberFormatException e)	{		JOptionPane.showMessageDialog(new JDialog(), "You need to enter correct rno");		txtrno.setText("");		txtrno.requestFocus();	}};btnSave.addActionListener(a2);Font f = new Font("Arial",Font.BOLD,24);Font f1 = new Font("Arial",Font.BOLD,15);lblrno.setFont(f1);txtrno.setFont(f1);btnSave.setFont(f);btnBack.setFont(f);setTitle("Delete St");setSize(400, 400);setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);setLocationRelativeTo(null);setVisible(true);c.setBackground(Color.BLUE);}}